import React, { useState, useEffect } from 'react';
import { getDatabase, ref, query, orderByChild, equalTo, get, remove } from 'firebase/database';
import { getAuth, onAuthStateChanged } from 'firebase/auth';
import './Syllabus.css';
import MainAcademics from './MainAcademics';

function Book() {
  const [semester, setSemester] = useState('');
  const [BookData, setBookData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleSemesterChange = (selectedSemester) => {
    setSemester(selectedSemester);
  };

  useEffect(() => {
    const auth = getAuth();
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setIsLoggedIn(true);
      } else {
        setIsLoggedIn(false);
      }
    });

    return () => unsubscribe();
  }, []);

  useEffect(() => {
    if (semester) {
      setLoading(true);
      setError(null);

      const db = getDatabase();
      const BookRef = ref(db, 'Book');
      const semesterQuery = query(BookRef, orderByChild('semester'), equalTo(semester));

      get(semesterQuery)
        .then((snapshot) => {
          setLoading(false);
          if (snapshot.exists()) {
            const data = snapshot.val();
            const formattedData = Object.keys(data).map((key) => ({ id: key, ...data[key] }));
            setBookData(formattedData);
          } else {
            setBookData([]);
          }
        })
        .catch((error) => {
          setLoading(false);
          console.error('Error fetching Book data:', error);
          setError('Error fetching Book data: ' + error.message);
        });
    }
  }, [semester]);

  const handleDelete = (id) => {
    const db = getDatabase();
    const BookRef = ref(db, `Book/${id}`);

    remove(BookRef)
      .then(() => {
        setBookData((prevData) => prevData.filter((item) => item.id !== id));
      })
      .catch((error) => {
        console.error('Error deleting Book data:', error);
        setError('Error deleting Book data: ' + error.message);
      });
  };

  return (
    <div className="syllabus-container">
      <MainAcademics />
      {isLoggedIn && (
        <h3 className='syllabus-header'>
          <a href="/addsyllabus">Click here to Add Syllabus</a>
        </h3>
      )}
      <h2 className="syllabus-header">Syllabus</h2>

      <div className="semester-buttons">
        {[...Array(8).keys()].map((i) => (
          <div
            key={i + 1}
            className={`semester-button ${semester === `${i + 1}` ? 'selected' : ''}`}
            onClick={() => handleSemesterChange(`${i + 1}`)}
          >
            <span>Semester {i + 1}</span>
          </div>
        ))}
      </div>

      {loading && <p className="loading-message">Loading syllabus data...</p>}
      {error && <p className="error-message">{error}</p>}

      {syllabusData.length > 0 ? (
        <table className="syllabus-table">
          <thead>
            <tr>
              <th>No</th>
              <th>Publication</th>
              <th>Name</th>
              <th>Year</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {syllabusData.map((item, index) => (
              <tr key={index}>
                <td>{item.no}</td>
                <td>{item.publication}</td>
                <td>{item.name}</td>
                <td>{item.year}</td>
                <td>
                  <a href={item.pdfUrl} target="_blank" rel="noopener noreferrer">View File</a>
                  {isLoggedIn && (
                    <button onClick={() => handleDelete(item.id)}>Delete</button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : !loading && !error && (
        <p className="no-data-message">No syllabus found for the selected semester.</p>
      )}
    </div>
  );
}

export default Book;
