import React, { useState, useEffect } from 'react';
import { getDatabase, ref, onValue, remove } from 'firebase/database';
import { getAuth, onAuthStateChanged } from 'firebase/auth';
import './ShowStudInfo.css';

const ShowStudInfo = () => {
  const [students, setStudents] = useState([]);
  const [filteredStudents, setFilteredStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const auth = getAuth();
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setIsLoggedIn(true);
      } else {
        setIsLoggedIn(false);
      }
    });

    return () => unsubscribe();
  }, []);

  useEffect(() => {
    const db = getDatabase();
    const studentsRef = ref(db, 'students');

    onValue(studentsRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const studentsList = Object.keys(data).map((key) => ({
          no: key,
          ...data[key],
        }));
        setStudents(studentsList);
        setFilteredStudents(studentsList); // Assuming you want to show all students initially
      }
      setLoading(false);
    });
  }, []);

  const handleDelete = (studentNo) => {
    const db = getDatabase();
    const studentRef = ref(db, `students/${studentNo}`);
    remove(studentRef)
      .then(() => {
        setFilteredStudents((prevStudents) =>
          prevStudents.filter((student) => student.no !== studentNo)
        );
      })
      .catch((error) => {
        console.error('Error deleting student:', error);
      });
  };

  return (
    <div className="show-stud-info-container">
      <h2>Student Information</h2>
      {loading ? (
        <p>Loading...</p>
      ) : filteredStudents.length > 0 ? (
        <table className="students-table">
          <thead>
            <tr>
              <th>Batch Type</th>
              <th>Enrollment No</th>
              <th>Name</th>
              <th>Email</th>
              <th>Year</th>
              <th>Achievement</th>
              {isLoggedIn && <th>Actions</th>}
            </tr>
          </thead>
          <tbody>
            {filteredStudents.map((student, index) => (
              <tr key={index}>
                <td>{student.batchType}</td>
                <td>{student.enrollmentNo}</td>
                <td>{student.name}</td>
                <td>{student.email}</td>
                <td>{student.year}</td>
                <td>{student.achievement}</td>
                {isLoggedIn && (
                  <td>
                    <button className="submit-button" onClick={() => handleDelete(student.no)}>Delete</button>
                  </td>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        !loading && <p className="no-data-message">No student info found.</p>
      )}
    </div>
  );
};

export default ShowStudInfo;
