import React, { useState, useEffect } from 'react';
import { getDatabase, ref, onValue, remove } from 'firebase/database';
import { getAuth, onAuthStateChanged } from 'firebase/auth';
import { useNavigate } from 'react-router-dom';
import './ShowCalender.css';
import MainAcademics from './MainAcademics';

function ShowCalender() {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const auth = getAuth();
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setIsLoggedIn(true);
      } else {
        setIsLoggedIn(false);
      }
    });

    return () => unsubscribe();
  }, []);

  useEffect(() => {
    const db = getDatabase();
    const calenderRef = ref(db, 'calender');

    onValue(calenderRef, (snapshot) => {
      const calenders = snapshot.val();
      if (calenders) {
        const formattedData = Object.keys(calenders).map((key) => ({ id: key, ...calenders[key] }));
        setData(formattedData);
      }
    });
  }, []);

  const handleDelete = (id) => {
    if (!isLoggedIn) return;
    const db = getDatabase();
    const calenderRef = ref(db, `calender/${id}`);

    remove(calenderRef)
      .then(() => {
        setData((prevData) => prevData.filter((item) => item.id !== id));
      })
      .catch((error) => {
        console.error('Error deleting calender:', error);
        setError('Error deleting calender: ' + error.message);
      });
  };

  const handleUpdate = (id) => {
    if (!isLoggedIn) return;
    navigate(`/updatecalender/${id}`);
  };

  return (
    <div className="calender-container">
      <MainAcademics />
      {isLoggedIn && (
        <h2><a href="/addcalender">Click here to Add Calender</a></h2>
      )}
      <h1 className="calender-header">Academic Calenders</h1>
      {error && <p className="error-message">{error}</p>}
      <table className="calender-table">
        <thead>
          <tr>
            <th>No</th>
            <th>Year</th>
            <th>Semester</th>
            <th>PDF</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => (
            <tr key={index}>
              <td>{item.no}</td>
              <td>{item.year}</td>
              <td>{item.semester}</td>
              <td>
                {item.pdfUrl && (
                  <a href={item.pdfUrl} target="_blank" rel="noopener noreferrer">
                    View PDF
                  </a>
                )}
              </td>
              <td>
                <button
                  onClick={() => handleDelete(item.id)}
                  disabled={!isLoggedIn}
                  style={{ cursor: isLoggedIn ? 'pointer' : 'not-allowed' }}
                >
                  Delete
                </button>
                <button
                  onClick={() => handleUpdate(item.id)}
                  disabled={!isLoggedIn}
                  style={{ cursor: isLoggedIn ? 'pointer' : 'not-allowed' }}
                >
                  Update
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ShowCalender;
