import React, { useState, useEffect } from 'react';
import { getDatabase, ref, get, update } from 'firebase/database';
import { getStorage, ref as storageRef, uploadBytes, getDownloadURL } from 'firebase/storage';
import { useNavigate, useParams } from 'react-router-dom';
import './AddSyllabus.css';

const UpdateAnalysis = () => {
  const { year, semester } = useParams();
  const [analysisData, setAnalysisData] = useState({
    pdf: null,
    pdfUrl: '',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAnalysisData = async () => {
      setLoading(true);
      setError(null);

      const db = getDatabase();
      const analysisRef = ref(db, `analysis/${year}/semesters/${semester}`);
      try {
        const snapshot = await get(analysisRef);
        setLoading(false);
        if (snapshot.exists()) {
          setAnalysisData(snapshot.val());
        } else {
          console.log('No data available');
        }
      } catch (error) {
        setLoading(false);
        console.error('Error fetching analysis data:', error);
        setError('Error fetching analysis data: ' + error.message);
      }
    };

    fetchAnalysisData();
  }, [year, semester]);

  const handleFileChange = (e) => {
    setAnalysisData({ ...analysisData, pdf: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await updateAnalysisData(analysisData);
    navigate('/showanalysis');
  };

  const updateAnalysisData = async (data) => {
    const db = getDatabase();
    const storage = getStorage();

    const uploadFile = async (file, existingUrl) => {
      if (file) {
        const fileRef = storageRef(storage, `analysis/${year}/${semester}/${file.name}`);
        const uploadTask = await uploadBytes(fileRef, file);
        return getDownloadURL(fileRef);
      }
      return existingUrl;
    };

    const pdfUrl = await uploadFile(data.pdf, data.pdfUrl);

    const analysisRef = ref(db, `analysis/${year}/semesters/${semester}`);
    const updatedAnalysisData = { ...data, pdfUrl };

    update(analysisRef, updatedAnalysisData)
      .then(() => {
        console.log('Data updated successfully!');
        navigate('/showanalysis');
      })
      .catch((error) => {
        console.error('Error updating data:', error);
        setError('Error updating data: ' + error.message);
      });
  };

  return (
    <div className="add-syllabus-container">
      <form className="add-syllabus-form" onSubmit={handleSubmit}>
        <h2>Update Analysis</h2>
        {loading && <p className="loading-message">Loading analysis data...</p>}
        {error && <p className="error-message">{error}</p>}
        <input type="file" name="pdf" onChange={handleFileChange} />
        <button type="submit">Update Analysis</button>
      </form>
    </div>
  );
};

export default UpdateAnalysis;
