          <Route path="/imageupload" element={<ImageUploader />} />
          <Route path="/imagedelete" element={<ImageDelete />} />
          <Route path="/signin" element={<SignIn />} />
        </Routes>
      </div>
      <Footer />
    </div>
  );
}

