import React, { useState, useEffect } from "react";
import { getAuth, onAuthStateChanged } from 'firebase/auth';
import { getDatabase, ref, get } from 'firebase/database';
import MainAbout from "./MainAbout";

const Pso = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
        const [aboutDeptData, setAboutDeptData] = useState('');
        
          useEffect(() => {
            const auth = getAuth();
            const unsubscribe = onAuthStateChanged(auth, (user) => {
              if (user) {
                setIsLoggedIn(true);
                fetchAboutDeptData();
              } else {
                setIsLoggedIn(false);
              }
            });
        
            return () => unsubscribe();
          }, []);
        
          const fetchAboutDeptData = async () => {
            const db = getDatabase();
            const messageRef = ref(db, 'aboutus/message');
            try {
              const snapshot = await get(messageRef);
              if (snapshot.exists()) {
                const data = snapshot.val();
                setAboutDeptData(data.pdfUrl);
              } else {
                console.log('No data available');
              }
            } catch (error) {
              console.error('Error fetching about dept data:', error);
            }
          };
    return (
        <div>
            <MainAbout />
            <h2>Programme Specific Objectives</h2>
           <p><b>
           The graduate from the department will be able to: </b>
            </p>
            <ol>
            <li> Develop and deploy efficient algorithms to solve engineering problems. </li>
            <li>Design, code, validate and test various application and system software and evaluate the performance based on performance metrics for software systems .</li>
            <li>Perform information retrieval and processing for real-time applications.</li>
            </ol>
            {isLoggedIn ? (
        aboutDeptData ? (
          <a href={aboutDeptData} target="_blank" rel="noopener noreferrer">View File</a>
        ) : (
          <p><br />Loading about department data...</p>
        )
      ) : (
        <p>You must be logged in to view this content.</p>
      )}
        </div>
    );
}
export default Pso;
