import React, { useState, useEffect } from 'react';
import { getDatabase, ref, query, orderByChild, equalTo, get, remove } from 'firebase/database';
import { getAuth, onAuthStateChanged } from 'firebase/auth';
import { useNavigate } from 'react-router-dom';
import './ShowAnalysis.css'; // Import the common CSS file
import MainAcademics from './MainAcademics';

const ShowAnalysis = () => {
  const [year, setYear] = useState('');
  const [analysisData, setAnalysisData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  const handleYearChange = (selectedYear) => {
    setYear(selectedYear);
  };

  useEffect(() => {
    const auth = getAuth();
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setIsLoggedIn(true);
      } else {
        setIsLoggedIn(false);
      }
    });

    return () => unsubscribe();
  }, []);

  useEffect(() => {
    if (year) {
      setLoading(true);
      setError(null);

      const db = getDatabase();
      const analysisRef = ref(db, 'analysis');
      const analysisQuery = query(analysisRef, orderByChild('year'), equalTo(year));

      get(analysisQuery)
        .then((snapshot) => {
          setLoading(false);
          if (snapshot.exists()) {
            const data = snapshot.val();
            const formattedData = Object.keys(data).map((key) => ({ id: key, ...data[key] }));
            setAnalysisData(formattedData);
          } else {
            setAnalysisData([]);
          }
        })
        .catch((error) => {
          setLoading(false);
          console.error('Error fetching analysis data:', error);
          setError('Error fetching analysis data: ' + error.message);
        });
    }
  }, [year]);

  const handleDelete = (id) => {
    if (!isLoggedIn) {
      alert('You must be logged in to delete analysis.');
      return;
    }

    const db = getDatabase();
    const analysisRef = ref(db, `analysis/${id}`);
    remove(analysisRef)
      .then(() => {
        setAnalysisData(analysisData.filter((item) => item.id !== id));
      })
      .catch((error) => {
        setError('Error deleting analysis: ' + error.message);
      });
  };

  const handleUpdate = (id) => {
    if (!isLoggedIn) {
      alert('You must be logged in to update analysis.');
      return;
    }

    navigate(`/updateanalysis/${id}`);
  };

  return (
    <div className="syllabus-container">
      <MainAcademics />
      {isLoggedIn && (
        <h2><a href="/addanalysis">Click here to Add Analysis</a></h2>
      )}
      <h1 className="syllabus-header">Result Analysis</h1>

      <div className="year-buttons">
        {['2021', '2022', '2023'].map((year) => (
          <button
            key={year}
            className={`year-button ${year === year ? 'selected' : ''}`}
            onClick={() => handleYearChange(year)}
          >
            {year}
          </button>
        ))}
      </div>

      {loading && <p className="loading-message">Loading analysis data...</p>}
      {error && <p className="error-message">{error}</p>}

      {analysisData.length > 0 ? (
        <table className="syllabus-table">
          <thead>
            <tr>
              <th>Year</th>
              <th>Semester</th>
              <th>PDF</th>
              {isLoggedIn && <th>Action</th>}
            </tr>
          </thead>
          <tbody>
            {analysisData.map((item, index) => (
              <tr key={index}>
                <td>{item.year}</td>
                <td>{item.semester}</td>
                <td>
                  {item.pdfUrl && (
                    <a href={item.pdfUrl} target="_blank" rel="noopener noreferrer">
                      View PDF
                    </a>
                  )}
                </td>
                {isLoggedIn && (
                  <td>
                    <button className="submit-button" onClick={() => handleDelete(item.id)}>Delete</button>
                    <button className="submit-button" onClick={() => handleUpdate(item.id)}>Update</button>
                  </td>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      ) : !loading && !error && (
        <p className="no-data-message">No analysis found for the selected year.</p>
      )}
    </div>
  );
};

export default ShowAnalysis;
