import React, { useState, useEffect } from 'react';
import { getDatabase, ref, push, onValue, remove } from 'firebase/database';
import { useNavigate } from 'react-router-dom';
import './studinfo.css'; // Import the CSS file

const StudInfo = () => {
  const [batchType, setBatchType] = useState('');
  const [enrollmentNo, setEnrollmentNo] = useState('');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [year, setYear] = useState('');
  const [achievement, setAchievement] = useState('');
  const [iep, setIep] = useState(''); // New IEP field
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [studentData, setStudentData] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const db = getDatabase();
    const studInfoRef = ref(db, 'studentInfo');

    onValue(studInfoRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const formattedData = Object.keys(data).map((key) => ({ id: key, ...data[key] }));
        setStudentData(formattedData);
      }
    });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const db = getDatabase();
    const studInfoRef = ref(db, 'studentInfo');

    const newStudentInfo = {
      batchType,
      enrollmentNo,
      name,
      email,
      year,
      achievement,
      iep, // Include IEP field
    };

    push(studInfoRef, newStudentInfo)
      .then(() => {
        setLoading(false);
        setBatchType('');
        setEnrollmentNo('');
        setName('');
        setEmail('');
        setYear('');
        setAchievement('');
        setIep(''); // Reset IEP field
      })
      .catch((error) => {
        setLoading(false);
        setError('Error adding student info: ' + error.message);
      });
  };

  const handleDelete = (id) => {
    const db = getDatabase();
    const studInfoRef = ref(db, `studentInfo/${id}`);
    remove(studInfoRef)
      .then(() => {
        setStudentData(studentData.filter((item) => item.id !== id));
      })
      .catch((error) => {
        setError('Error deleting student info: ' + error.message);
      });
  };

  const handleUpdate = (id) => {
    navigate(`/updatestudinfo/${id}`);
  };

  return (
    <div className="add-student-info">
      <h2>Add Student Info</h2>
      {error && <p className="error-message">{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Batch Type:</label>
          <input
            type="text"
            value={batchType}
            onChange={(e) => setBatchType(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Enrollment No:</label>
          <input
            type="text"
            value={enrollmentNo}
            onChange={(e) => setEnrollmentNo(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Year:</label>
          <input
            type="text"
            value={year}
            onChange={(e) => setYear(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Achievement:</label>
          <input
            type="text"
            value={achievement}
            onChange={(e) => setAchievement(e.target.value)}
            required
          />
        </div>
        <div>
          <label>IEP:</label>
          <input
            type="text"
            value={iep}
            onChange={(e) => setIep(e.target.value)}
            required
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Adding...' : 'Add Student Info'}
        </button>
      </form>

      <h2>Student Info List</h2>
      {studentData.length > 0 ? (
        <table className="student-info-table">
          <thead>
            <tr>
              <th>Batch Type</th>
              <th>Enrollment No</th>
              <th>Name</th>
              <th>Email</th>
              <th>Year</th>
              <th>Achievement</th>
              <th>IEP</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {studentData.map((item) => (
              <tr key={item.id}>
                <td>{item.batchType}</td>
                <td>{item.enrollmentNo}</td>
                <td>{item.name}</td>
                <td>{item.email}</td>
                <td>{item.year}</td>
                <td>{item.achievement}</td>
                <td>{item.iep}</td>
                <td>
                  <button onClick={() => handleDelete(item.id)}>Delete</button>
                </td>
                  <button onClick={() => handleUpdate(item.id)}>Update</button>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No student info available.</p>
      )}
    </div>
  );
};

export default StudInfo;
