import React, { useState, useEffect } from "react";
import { getDatabase, ref, get } from 'firebase/database';
import MainAbout from "./MainAbout";

const Pso = () => {
  const [psoData, setPsoData] = useState('');
  
    useEffect(() => {
      fetchPsoData();
    }, []);
  
    const fetchPsoData = async () => {
      const db = getDatabase();
      const psoRef = ref(db, 'aboutus/pso');
      try {
        const snapshot = await get(psoRef);
        if (snapshot.exists()) {
          const data = snapshot.val();
          setPsoData(data.htmlContent);
        } else {
          console.log('No data available');
        }
      } catch (error) {
        console.error('Error fetching PO data:', error);
      }
    };
    return (
        <div>
            <MainAbout />
            <h2>Programme Specific Objectives</h2>
            {aboutDeptData ? (
              <a href={aboutDeptData} target="_blank" rel="noopener noreferrer">View File</a>
            ) : (
              <p><br />Loading about department data...</p>
            )}
        </div>
    );
}
export default Pso;
