import React, { useState, useEffect } from "react";
import { getAuth, onAuthStateChanged } from 'firebase/auth';
import { getDatabase, ref, get } from 'firebase/database';
import MainAbout from "./MainAbout";

const Peo = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
        const [aboutDeptData, setAboutDeptData] = useState('');
        
          useEffect(() => {
            const auth = getAuth();
            const unsubscribe = onAuthStateChanged(auth, (user) => {
              if (user) {
                setIsLoggedIn(true);
                fetchAboutDeptData();
              } else {
                setIsLoggedIn(false);
              }
            });
        
            return () => unsubscribe();
          }, []);
        
          const fetchAboutDeptData = async () => {
            const db = getDatabase();
            const peoRef = ref(db, 'aboutus/peo');
            try {
              const snapshot = await get(peoRef);
              if (snapshot.exists()) {
                const data = snapshot.val();
                setAboutDeptData(data.pdfUrl);
              } else {
                console.log('No data available');
              }
            } catch (error) {
              console.error('Error fetching about dept data:', error);
            }
          };
    return (
        <div>
            <MainAbout />
            <h2>Programme Educational Objectives</h2>
           <p> The educational objectives of the Information Technology program are designed to produce competent engineers who are ready to contribute effectively to the advancement of Information Technology causes and to accommodate the needs of the profession.</p>
           <p><b>
            The highlights of the objectives include: </b>
            </p>
            <ol>
            <li> Endorse the practice of Information Technology in the general disciplines of design, development and deployment of software and integration of existing technologies. </li>
            <li>Teach the application of fundamental engineering skills and specialized computing knowledge to provide feasible solutions to problems in various areas of IT. </li>
            <li>Provide knowledge to students beyond the prescribed curriculum.</li>
            <li>Regular organization of technical guest-lectures, programming contests, various quiz, seminars, short-term training programme, conference, workshops and cultural programme for an all-round development of students.</li>
            </ol>
            {isLoggedIn ? (
        aboutDeptData ? (
          <a href={aboutDeptData} target="_blank" rel="noopener noreferrer">View File</a>
        ) : (
          <p><br />Loading about department data...</p>
        )
      ) : (
        <p>You must be logged in to view this content.</p>
      )}
        </div>
    );
}
export default Peo;
