import React, { useState, useEffect } from 'react';
import { getDatabase, ref, update, get } from 'firebase/database';
import { useNavigate } from 'react-router-dom';
import { getStorage, ref as storageRef, uploadBytes, getDownloadURL } from 'firebase/storage';
import './ShowAnalysis.css';

function AddAnalysis() {
  const [year, setYear] = useState('');
  const [semester, setSemester] = useState('');
  const [resultPdf, setResultPdf] = useState(null);
  const [lastId, setLastId] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    fetchLastId();
  }, []);

  const fetchLastId = async () => {
    const db = getDatabase();
    const lastIdRef = ref(db, 'analysis/lastId');
    try {
      const snapshot = await get(lastIdRef);
      if (snapshot.exists()) {
        setLastId(snapshot.val());
      } else {
        setLastId(0);
      }
    } catch (error) {
      console.error('Error fetching last ID:', error);
    }
  };

  const handleFileChange = (e) => {
    setResultPdf(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const db = getDatabase();
    const storage = getStorage();

    const uploadFile = async (file) => {
      if (file) {
        const fileRef = storageRef(storage, `analysis/${year}/${semester}-${file.name}`);
        await uploadBytes(fileRef, file);
        return getDownloadURL(fileRef);
      }
      return null;
    };

    const resultPdfUrl = await uploadFile(resultPdf);

    if (resultPdfUrl) {
      const newId = lastId + 1;
      const updates = {};
      updates[`analysis/${newId}`] = {
        year,
        semester,
        pdfUrl: resultPdfUrl,
      };
      updates['analysis/lastId'] = newId;

      await update(ref(db), updates);

      // Reset the form
      setYear('');
      setSemester('');
      setResultPdf(null);
      navigate('/showanalysis');
    }
  };

  return (
    <div className="syllabus-container">
      <h1 className="syllabus-header">Add Result Analysis</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Year:</label>
          <input type="text" value={year} onChange={(e) => setYear(e.target.value)} required />
        </div>
        <div className="form-group">
          <label>Semester:</label>
          <input type="text" value={semester} onChange={(e) => setSemester(e.target.value)} required />
        </div>
        <div className="form-group">
          <label>Result PDF:</label>
          <input type="file" onChange={handleFileChange} required />
        </div>
        <button type="submit" className="submit-button">Add Result</button>
      </form>
    </div>
  );
}

export default AddAnalysis;
