import React, { useState, useEffect } from "react";
import { getAuth, onAuthStateChanged } from 'firebase/auth';
import { getDatabase, ref, get } from 'firebase/database';
import MainAbout from "./MainAbout";

const Mission = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
      const [aboutDeptData, setAboutDeptData] = useState('');
      
        useEffect(() => {
          const auth = getAuth();
          const unsubscribe = onAuthStateChanged(auth, (user) => {
            if (user) {
              setIsLoggedIn(true);
              fetchAboutDeptData();
            } else {
              setIsLoggedIn(false);
            }
          });
      
          return () => unsubscribe();
        }, []);
      
        const fetchAboutDeptData = async () => {
          const db = getDatabase();
          const messageRef = ref(db, 'aboutus/mission');
          try {
            const snapshot = await get(messageRef);
            if (snapshot.exists()) {
              const data = snapshot.val();
              setAboutDeptData(data.pdfUrl);
            } else {
              console.log('No data available');
            }
          } catch (error) {
            console.error('Error fetching about dept data:', error);
          }
        };
  return (
    <><MainAbout />
      <div className="bg-[#F2F2F2] py-16">
        <h2>Mission</h2>
        <ul>
          <li>To impart sound technical knowledge in the field of Information Technology through a creative balance of academic, research and co-curricular activities.</li>
          <li>To cultivate professional ethics and soft skills in the student for global competitiveness.</li>
          <li>To collaborate with industries, government entities and other academic institutions to bring socially responsible, sustainable IT solutions to the world.</li>
        </ul>
        {isLoggedIn ? (
        aboutDeptData ? (
          <a href={aboutDeptData} target="_blank" rel="noopener noreferrer">View File</a>
        ) : (
          <p><br />Loading about department data...</p>
        )
      ) : (
        <p>You must be logged in to view this content.</p>
      )}
      </div></>
  );
};
export default Mission;
