import React, { useState, useEffect } from 'react';
import { getDatabase, ref, set, get } from 'firebase/database';
import { getStorage, ref as storageRef, uploadBytes, getDownloadURL } from 'firebase/storage';
import { useNavigate } from 'react-router-dom';
import './AddSyllabus.css';

const AddAnalysis = () => {
  const [analysisData, setAnalysisData] = useState({
    year: '',
    semester: '',
    pdf: null,
  });
  const [uploadProgress, setUploadProgress] = useState(0);
  const [retrievedData, setRetrievedData] = useState(null);
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setAnalysisData({ ...analysisData, [name]: value });
  };

  const handleFileChange = (e) => {
    setAnalysisData({ ...analysisData, pdf: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await writeAnalysisData(analysisData);
    navigate('/showanalysis');
  };

  const writeAnalysisData = async (data) => {
    const db = getDatabase();
    const storage = getStorage();

    const uploadFile = async (file) => {
      if (file) {
        const fileRef = storageRef(storage, `analysis/${file.name}`);
        const uploadTask = await uploadBytes(fileRef, file);
        return getDownloadURL(fileRef);
      }
      return null;
    };

    const pdfUrl = await uploadFile(data.pdf);

    const analysisRef = ref(db, 'analysis/' + data.year + '_' + data.semester);
    const newAnalysisData = { ...data, pdfUrl };

    set(analysisRef, newAnalysisData)
      .then(() => {
        console.log('Data written successfully!');
      })
      .catch((error) => {
        console.error('Error writing data:', error);
      });
  };

  const fetchAnalysisData = async () => {
    const db = getDatabase();
    const analysisRef = ref(db, 'analysis/');
    try {
      const snapshot = await get(analysisRef);
      if (snapshot.exists()) {
        setRetrievedData(snapshot.val());
      } else {
        console.log('No data available');
      }
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchAnalysisData();
  }, []);

  return (
    <div className="add-syllabus-container">
      <form className="add-syllabus-form" onSubmit={handleSubmit}>
        <input type="text" name="year" placeholder="Year" value={analysisData.year} onChange={handleInputChange} required />
        <input type="text" name="semester" placeholder="Semester" value={analysisData.semester} onChange={handleInputChange} required />
        <input type="file" name="pdf" onChange={handleFileChange} required />
        <button type="submit">Upload Analysis</button>
      </form>
    </div>
  );
};

export default AddAnalysis;
