import React, { useState, useEffect } from "react";
import { getDatabase, ref, get } from 'firebase/database';
import MainAbout from "./MainAbout";

const Po = () => {
  const [poData, setPoData] = useState('');

  useEffect(() => {
    fetchPoData();
  }, []);

  const fetchPoData = async () => {
    const db = getDatabase();
    const poRef = ref(db, 'aboutus/po');
    try {
      const snapshot = await get(poRef);
      if (snapshot.exists()) {
        const data = snapshot.val();
        setPoData(data.htmlContent); // Assuming htmlContent contains the HTML formatted text
      } else {
        console.log('No data available');
      }
    } catch (error) {
      console.error('Error fetching PO data:', error);
    }
  };

  return (
    <div>
      <MainAbout />
      <h2>Programme Objectives</h2>
      {poData ? (
        <div dangerouslySetInnerHTML={{ __html: poData }} />
      ) : (
        <p><br />Loading Programme Objectives data...</p>
      )}
    </div>
  );
}

export default Po;
