import React, { useState, useEffect } from 'react';
import { getStorage, ref as storageRef, uploadBytes, getDownloadURL } from 'firebase/storage';
import { getAuth, onAuthStateChanged } from 'firebase/auth';
import { useNavigate } from 'react-router-dom';
import './UpdateAbout.css';

const UpdateAbout = () => {
  const [selectedSection, setSelectedSection] = useState('');
  const [file, setFile] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const auth = getAuth();
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setIsLoggedIn(true);
      } else {
        setIsLoggedIn(false);
      }
    });

    return () => unsubscribe();
  }, []);

  const handleSectionChange = (e) => {
    setSelectedSection(e.target.value);
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file || !selectedSection) return;

    const storage = getStorage();
    const fileRef = storageRef(storage, `aboutus/${selectedSection}/${file.name}`);
    await uploadBytes(fileRef, file);
    const fileUrl = await getDownloadURL(fileRef);

    console.log('File uploaded successfully:', fileUrl);
    navigate('/');
  };

  return (
    <div className="upload-aboutus-container">
      {isLoggedIn ? (
        <form className="upload-aboutus-form" onSubmit={handleSubmit}>
          <h2>Upload File for About Us Section</h2>
          <div>
            <label htmlFor="section">Select Section:</label>
            <select id="section" value={selectedSection} onChange={handleSectionChange} required>
              <option value="">--Select Section--</option>
              <option value="about-dept">About Dept</option>
              <option value="message">Message</option>
              <option value="vision">Vision</option>
              <option value="mission">Mission</option>
              <option value="pso">PSO</option>
              <option value="po">PO</option>
              <option value="peo">PEO</option>
            </select>
          </div>
          <div>
            <label htmlFor="file">Upload File:</label>
            <input type="file" id="file" onChange={handleFileChange} required />
          </div>
          <button type="submit">Upload</button>
        </form>
      ) : (
        <p className="error-message">You must be logged in to upload files.</p>
      )}
    </div>
  );
};

export default UpdateAbout;
