import React, { useState, useEffect } from "react";
import { getAuth, onAuthStateChanged } from 'firebase/auth';
import { getDatabase, ref, get } from 'firebase/database';
import MainAbout from "./MainAbout";

const Vision = () => {
    const [aboutDeptData, setAboutDeptData] = useState('');
    
      useEffect(() => {
        fetchAboutDeptData();
      }, []);
    
      const fetchAboutDeptData = async () => {
        const db = getDatabase();
        const aboutDeptRef = ref(db, 'aboutus/vision');
        try {
          const snapshot = await get(aboutDeptRef);
          if (snapshot.exists()) {
            const data = snapshot.val();
            setAboutDeptData(data.pdfUrl);
          } else {
            console.log('No data available');
          }
        } catch (error) {
          console.error('Error fetching about dept data:', error);
        }
      };
    return (
        <div>
        <MainAbout />
        <h2> Vision</h2>
        <p>" To evolve as a global center of excellence in the field of Information Technology imparting technical education and professional ethics to thrive in an era of globalization."</p>
        {aboutDeptData ? (
        <a href={aboutDeptData} target="_blank" rel="noopener noreferrer">View File</a>
      ) : (
        <p><br />Loading about department data...</p>
      )}
        </div>
    );
}

export default Vision;
