import React, { useState, useEffect } from 'react';
import { getDatabase, ref, get, update } from 'firebase/database';
import { useNavigate, useParams } from 'react-router-dom';
import './studinfo.css'; // Import the CSS file

const UpdateStudInfo = () => {
  const { id } = useParams();
  const [studentData, setStudentData] = useState({
    batchType: '',
    enrollmentNo: '',
    name: '',
    email: '',
    year: '',
    achievement: '',
    iep: '',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchStudentData = async () => {
      setLoading(true);
      setError(null);

      const db = getDatabase();
      const studInfoRef = ref(db, `studentInfo/${id}`);
      try {
        const snapshot = await get(studInfoRef);
        setLoading(false);
        if (snapshot.exists()) {
          setStudentData(snapshot.val());
        } else {
          console.log('No data available');
        }
      } catch (error) {
        setLoading(false);
        console.error('Error fetching student data:', error);
        setError('Error fetching student data: ' + error.message);
      }
    };

    fetchStudentData();
  }, [id]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setStudentData({ ...studentData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const db = getDatabase();
    const studInfoRef = ref(db, `studentInfo/${id}`);

    update(studInfoRef, studentData)
      .then(() => {
        setLoading(false);
        navigate('/showstud');
      })
      .catch((error) => {
        setLoading(false);
        setError('Error updating student info: ' + error.message);
      });
  };

  return (
    <div className="add-student-info">
      <h2>Update Student Info</h2>
      {error && <p className="error-message">{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Batch Type:</label>
          <input
            type="text"
            name="batchType"
            value={studentData.batchType}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Enrollment No:</label>
          <input
            type="text"
            name="enrollmentNo"
            value={studentData.enrollmentNo}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={studentData.name}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={studentData.email}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Year:</label>
          <input
            type="text"
            name="year"
            value={studentData.year}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Achievement:</label>
          <input
            type="text"
            name="achievement"
            value={studentData.achievement}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>IEP:</label>
          <input
            type="text"
            name="iep"
            value={studentData.iep}
            onChange={handleInputChange}
            required
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Updating...' : 'Update Student Info'}
        </button>
      </form>
    </div>
  );
};

export default UpdateStudInfo;
