import React, { useState, useEffect } from 'react';
import { getDatabase, ref, set, get, remove } from 'firebase/database';
import { getStorage, ref as storageRef, uploadBytes, getDownloadURL } from 'firebase/storage';
import { useNavigate } from 'react-router-dom';
import './AddSyllabus.css';

const AddBook = () => {
  const [syllabusData, setSyllabusData] = useState({
    no: '',
    publication: '',
    name: '',
    year: '',
    pdf: null,
  });
  const [retrievedData, setRetrievedData] = useState(null);
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSyllabusData({ ...syllabusData, [name]: value });
  };

  const handleFileChange = (e) => {
    setSyllabusData({ ...syllabusData, pdf: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await writeSyllabusData(syllabusData);
    fetchSyllabusData();
  };

  const writeSyllabusData = async (data) => {
    const db = getDatabase();
    const storage = getStorage();

    const uploadFile = async (file) => {
      if (file) {
        const fileRef = storageRef(storage, `syllabus/${file.name}`);
        const uploadTask = await uploadBytes(fileRef, file);
        return getDownloadURL(fileRef);
      }
      return null;
    };

    const pdfUrl = await uploadFile(data.pdf);

    const syllabusRef = ref(db, 'syllabus/' + data.no);
    const newSyllabusData = { ...data, pdfUrl };

    set(syllabusRef, newSyllabusData)
      .then(() => {
        console.log('Data written successfully!');
      })
      .catch((error) => {
        console.error('Error writing data:', error);
      });
  };

  const fetchSyllabusData = async () => {
    const db = getDatabase();
    const syllabusRef = ref(db, 'book/');
    try {
      const snapshot = await get(syllabusRef);
      if (snapshot.exists()) {
        setRetrievedData(snapshot.val());
      } else {
        console.log('No data available');
      }
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleDelete = async (no) => {
    const db = getDatabase();
    const syllabusRef = ref(db, 'book/' + no);
    try {
      await remove(syllabusRef);
      fetchSyllabusData();
      console.log('Data deleted successfully!');
    } catch (error) {
      console.error('Error deleting data:', error);
    }
  };

  useEffect(() => {
    fetchSyllabusData();
  }, []);

  return (
    <div className="add-syllabus-container">
      <form className="add-syllabus-form" onSubmit={handleSubmit}>
        <h2>Add Syllabus</h2>
        <input type="text" name="no" placeholder="No" value={syllabusData.no} onChange={handleInputChange} required />
        <input type="text" name="publication" placeholder="Publication" value={syllabusData.publication} onChange={handleInputChange} required />
        <input type="text" name="name" placeholder="Name" value={syllabusData.name} onChange={handleInputChange} required />
        <input type="text" name="year" placeholder="Year" value={syllabusData.year} onChange={handleInputChange} required />
        <input type="file" name="pdf" onChange={handleFileChange} required />
        <button type="submit">Upload Syllabus</button>
      </form>
      {retrievedData && (
        <table className="syllabus-table">
          <thead>
            <tr>
              <th>No</th>
              <th>Publication</th>
              <th>Name</th>
              <th>Year</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {Object.keys(retrievedData).map((key) => (
              <tr key={key}>
                <td>{retrievedData[key].no}</td>
                <td>{retrievedData[key].publication}</td>
                <td>{retrievedData[key].name}</td>
                <td>{retrievedData[key].year}</td>
                <td>
                  <a href={retrievedData[key].pdfUrl} target="_blank" rel="noopener noreferrer">View File</a>
                  <button onClick={() => handleDelete(retrievedData[key].no)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default AddBook;
